      out.write("\t\tfunction showSlide(n){\r\n");
      out.write("\t\t\tif(slideIndex > slides.length -1){\r\n");
      out.write("\t\t\t\tslideIndex = 0;\r\n");
      out.write("\t\t\t}\r\n");
      out.write("\t\t\tif(slideIndex < 0){\r\n");
