      out.write("\t\t\t}\r\n");
      out.write("\t\t\tfor(let i=0; i<slides.length; i++){\r\n");
      out.write("\t\t\t\tslides[i].style.display =\"none\";\r\n");
      out.write("\t\t\t}\r\n");
      out.write("\t\t\tfor(let i=0; i<dots.length; i++){\r\n");
      out.write("\t\t\t\tdots[i].className = dots[i].className.replace(\" active\", \"\");\r\n");
      out.write("\t\t\t}\r\n");
      out.write("\t\t\tslides[slideIndex].style.display = \"block\";\r\n");
      out.write("\t\t\tdots[slideIndex].className += \" active\";\r\n");
      out.write("\t\t}\r\n");
      out.write("\t\tdots.forEach((item, index) =>{\r\n");
      out.write("\t\t\titem.addEventListener('click', ()=>{\r\n");
      out.write("\t\t\t\tshowSlide(slideIndex = index);\r\n");
      out.write("\t\t\t})\r\n");
